import { createSlice } from '@reduxjs/toolkit'


const photo = creacteSlice({
    name: 'photos',
    initialState: [],
    reducers: {
        addPhoto: (state, action) => {
            state.push(action.payload)
        }
    }
})

const { reducer, actions } = photo
export const { addPhoto } = actions
export default reducer;
