  }
  const photos = useSelector(state => state.photos)

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <input name="name" ref={register} placeholder=" name" value={name} onChange={handleChangeName} />
        <br />
        <input name="adress" ref={register} placeholder="adress" value={adress} onChange={handleChangeAdress} />
        <br />

        <input type="submit" />
      </form>
      <div className='d-flex justify-content-center'>
        <table>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Adress</th>
            <th>Action</th>
          </tr>
          {photos.map(photo => {
            return (
              <tr>
                <td>{photo.id} </td>

                <td>{photo.name} </td>
                <td>{photo.adress} </td>
                <td> <button onClick={() => deletePhoto(photo)}>Delete</button> </td>
              </tr>
            )
          })}
        </table>
      </div>
    </div>
  );
}

export default PhotoForm
