import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { useDispatch, useSelector } from 'react-redux'
import { addPhoto, removePhoto, updatePhoto } from 'components/phooApp/PhotoSlice'
const PhotoForm = () => {
  const { register, handleSubmit } = useForm();
  const dispatch = useDispatch()
  // const [name, setName] = useState()
  // const [adress, setAdress] = useState()
  // const [id, setId] = useState()
  // const [isAdd, setUserInfo] = useState()
  const [addOrEdit, setAddOrEdit] = useState(true)
  const [userRequest, setUserRequest] = useState({
    id: '',
    name: '',
    adress: '',
  });
  const { id, name, adress } = userRequest
  const onSubmit = (data) => {
    if (addOrEdit) {
      const newData = {
        id: 1 + (Math.random() * 99),
        name: data.name,
        adress: data.adress
      }
      setUserRequest({
        id: '',
        name: '',
        adress: ''
      })

      dispatch(addPhoto(newData))
    } else {
      console.log(data, 'data');
      const action = updatePhoto(data)
      dispatch(action)
      setAddOrEdit(!addOrEdit)
      // setName('')
      // setAdress('')
      setUserRequest({
        id: '',
        name: '',
        adress: ''
      })
    }
  };
  // const handleChange = (e) => {
  //   setName(e.target.value)
  //   setAdress(e.target.value)
  // }

  // const deletePhoto = (photo) => {
  //   console.log(photo);
  //   const removePhotoId = photo.id
  //   const action = removePhoto(removePhotoId)
  //   dispatch(action)
  //   console.log(photos, '====');
  // }
  // const editbutton = (photo) => {
  //   setAddOrEdit(!addOrEdit)
  //   setId(photo.id)
  //   setName(photo.name)
  //   setAdress(photo.adress)
  // }
  const photos = useSelector(state => state.photos)   // getbyID = filter

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <input style={{ display: 'none' }} name="id" ref={register} placeholder=" id" value={id} /><br />
        <input name="name" ref={register} placeholder=" name" value={name} />
        <br />
        <input name="adress" ref={register} placeholder="adress" value={adress} />
        <br />
        {addOrEdit ? <input type="submit" value='Add' /> : <input value='Edit' type='submit' />}

      </form>
      <div className='d-flex justify-content-center'>
        <table>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Adress</th>
            <th>Action</th>
          </tr>
          {photos.map(photo => {
            return (
              <tr>
                <td>{photo.id} </td>

                <td>{photo.name} </td>
                <td>{photo.adress} </td>
                {/* <td> <button onClick={() => deletePhoto(photo)}>Delete</button> <button onClick={() => editbutton(photo)}>Edit</button> </td> */}


              </tr>
            )
          })}
        </table>
      </div>
    </div>
  );
}

export default PhotoForm
