import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { useDispatch, useSelector } from 'react-redux'
import { addPhoto, removePhoto, updatePhoto } from 'components/phooApp/PhotoSlice'
const PhotoForm = () => {
  const { register, handleSubmit } = useForm();
  const dispatch = useDispatch()
  const [name, setName] = useState()
  const [adress, setAdress] = useState()
  const [addOrEdit, setAddOrEdit] = useState(true)
  const onSubmit = (data) => {
    if (addOrEdit) {
      const newData = {
        id: 1 + (Math.random() * 99),
        name: data.name,
        adress: data.adress
      }
      setName('')
      setAdress('')
      const action = addPhoto(newData)
      dispatch(action)
    } else {
      console.log(data);
    }
  };
  const handleChangeName = (e) => {
    setName(e.target.value)
  }
  const handleChangeAdress = (e) => {
    setAdress(e.target.value)
  }
  const deletePhoto = (photo) => {
    console.log(photo);
    const removePhotoId = photo.id
    const action = removePhoto(removePhotoId)
    dispatch(action)
    console.log(photos, '====');
  }
  const updatePhoto = (photo) => {
    setAddOrEdit(!addOrEdit)
    setName(photo.name)
    setAdress(photo.adress)
  }
  const photos = useSelector(state => state.photos)

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <input name="name" ref={register} placeholder=" name" value={name} onChange={handleChangeName} />
        <br />
        <input name="adress" ref={register} placeholder="adress" value={adress} onChange={handleChangeAdress} />
        <br />
        {addOrEdit ? <input type="submit" /> : <button type='submit'>Edit</button>} &nbsp;&nbsp;

      </form>
      <div className='d-flex justify-content-center'>
        <table>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Adress</th>
            <th>Action</th>
          </tr>
          {photos.map(photo => {
            return (
              <tr>
                <td>{photo.id} </td>

                <td>{photo.name} </td>
                <td>{photo.adress} </td>
                <td> <button onClick={() => deletePhoto(photo)}>Delete</button> <button onClick={() => updatePhoto(photo)}>Edit</button> </td>


              </tr>
            )
          })}
        </table>
      </div>
    </div>
  );
}

export default PhotoForm
