import React from 'react';
import { Formik } from 'formik';

const PhotoForm = () => {
    const handleSubmit = (valuse) => {
        console.log(valuse);
    }
    return (
        <div>
            <h1>Anywhere in your app!</h1>
            <Formik
                initialValues={{ title: '', password: '' }}
                validate={values => {
                    const errors = {};
                    if (!values.title) {
                        errors.title = 'Required';
                    } else if (
                        !/^[A-Z0-9]{3,}/i.test(values.title)
                    ) {
                        errors.title = 'it nhat 2';
                    }
                    return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                    setTimeout(() => {
                        // alert(JSON.stringify(values, null, 2));
                        handleSubmit
                        // alert(values.title);
                        setSubmitting(false);
                    }, 400);
                }}
            >
                {({
                    values,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    isSubmitting,
                    /* and other goodies */
                }) => (
                        <form onSubmit={handleSubmit}>
                            <input
                                type="title"
                                name="title"
                                onChange={handleChange}
                                onBlur={handleBlur}
                                value={values.title}
                            />
                            {errors.title && touched.title && errors.title}
                            <input
                                type="password"
                                name="password"
                                onChange={handleChange}
                                onBlur={handleBlur}
                                value={values.password}
                            />
                            {errors.password && touched.password && errors.password}
                            <button type="submit" disabled={isSubmitting}>
                                Submit
           </button>
                        </form>
                    )}
            </Formik>
        </div>
    );
};

export default PhotoForm
