import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { useDispatch, useSelector } from 'react-redux'
import { addPhoto } from 'components/phooApp/PhotoSlice'
const PhotoForm = () => {
  const { register, handleSubmit } = useForm();
  const dispatch = useDispatch()
  const [name, setName] = useState()
  const [adress, setAdress] = useState()
  const onSubmit = (data) => {

    const newData = {
      id: 1 + (Math.random() * 99),
      name: data.name,
      adress: data.adress
    }
    handleChangeName('')
    setAdress('')
    const action = addPhoto(newData)
    dispatch(action)
  };
  const handleChangeName = (e) => {

    setName(e.target.value)

  }
  const handleChangeAdress = (e) => {

  }

  const photos = useSelector(state => state.photos)
  console.log(photos, '====>');
  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <input name="name" ref={register} placeholder=" name" onChange={handleChangeName} />
        <br />
        <input name="adress" ref={register} placeholder="adress" onChange={handleChangeAdress} />
        <br />

        <input type="submit" />
      </form>
      <div className='d-flex justify-content-center'>
        <table>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Adress</th>
          </tr>
          {photos.map(photo => {
            return (
              <tr>
                <td>{photo.id} </td>

                <td>{photo.name} </td>
                <td>{photo.adress} </td>
              </tr>
            )
          })}
        </table>
      </div>
    </div>
  );
}

export default PhotoForm
